query.mssql:
if not exists(select 1 from [dbo].[Sungero_Docflow_Params] where [Key] = '{0}')
  insert [Sungero_Docflow_Params] ([Key],Value) values ('{0}', '{1}')
else UPDATE Sungero_Docflow_Params SET Value = '{1}' WHERE ([Key] = '{0}')

query.postgres:
do $$
begin
  if not exists(select 1 from Sungero_Docflow_Params where Key = '{0}')
  then
    insert into Sungero_Docflow_Params (Key, Value) values ('{0}', '{1}');
  else
    UPDATE Sungero_Docflow_Params SET Value = '{1}' WHERE (Key = '{0}');
  end if;
end$$;

query.mssql:
update DirRX_Projec1_PrjctActivity set DirRX_Projec1_PrjctActivity.ProjectPlan = temp_projectplanner.projectplan_name from temp_projectplanner, DirRX_Projec1_PrjctActivity where temp_projectplanner.id = DirRX_Projec1_PrjctActivity.id;
truncate table temp_projectplanner
update Sungero_Docflow_Project set Sungero_Docflow_Project.ProjectPlanDir_Project_DirRX = temp_projectplanner_projects.projectplan_name from temp_projectplanner_projects, Sungero_Docflow_Project where temp_projectplanner_projects.id = Sungero_Docflow_Project.id;
truncate table temp_projectplanner_projects

query.postgres:
update DirRX_Projec1_PrjctActivity
    set ProjectPlan = temp_projectplanner.projectplan_name
from temp_projectplanner
where temp_projectplanner.id = DirRX_Projec1_PrjctActivity.id;

truncate table temp_projectplanner;

update Sungero_Docflow_Project
    set ProjectPlanDir_Project_DirRX = temp_projectplanner_projects.projectplan_name
from temp_projectplanner_projects
where temp_projectplanner_projects.id = Sungero_Docflow_Project.id;

truncate table temp_projectplanner_projects;

query.mssql:
if not exists (select * from sysobjects where name='ResourceLinks')
    CREATE TABLE ResourceLinks (
   project_activity_id int NOT NULL,
   resource_id int NOT NULL,
   average_busy real NOT NULL
);

query.postgres:
CREATE TABLE IF NOT EXISTS ResourceLinks (
   project_activity_id int NOT NULL,
   resource_id int NOT NULL,
   average_busy real NOT NULL
);
